function drawSine(p1, p2,freq,amp)
{
  let d = p1.dist(p2);
  let a = atan2(p2.y-p1.y,p2.x-p1.x);
  push();
  translate(p1.x,p1.y);
  stroke(0);
  rotate(a);
  beginShape();
  for (let i = 0; i <= d; i += d/100) {
    let xx = i;
    let yy = sin(xx*TWO_PI*freq/d)*amp;

    vertex(xx,yy);
  }
  endShape(CLOSE);
  pop();
}

