// https://en.wikipedia.org/wiki/Hippopede

function drawHipo(x,y,b,scale) {
  a = b/1.5;
  noFill();
  strokeWeight(0.25);
  beginShape();
  for(let ang = 0; ang <= 2*PI;ang+=(2*PI)/200)
  {
       let xx = x + scale*r(ang,a,b)*cos(ang);
       let yy = y + scale*r(ang,a,b)*sin(ang);
       vertex(xx,yy);
  }
  endShape();
}

function r(theta,a,b) 
{
  return (4*b*(a - b*sin(theta)**2))**0.5;
}


