function curva(x, y) {

  addBlur();

  noStroke();
  let freq = random(0.1, 2.0);
  let size = random(0.1, 20);
  let amp = random(50, 200);
  let range = gridSize - 5;
  let ww = random(0, 0.25);
  let step = 0.01;
  let pos = createVector(x, y);
  let p1 = createVector(pos.x - range / 2, pos.y - range / 2);
  let p2 = createVector(pos.x + range / 2, pos.y + range / 2);

  let gradient;

  gradient = drawingContext.createRadialGradient(p1.x, p1.y, 0, p2.x, p2.y, range * 2);

  for (let i = 0; i < 1.0; i += step) {
    gradient.addColorStop(i, generateColor(10));
  }
  drawingContext.fillStyle = gradient;


  rect(pos.x - range / 4, pos.y, range / 2.35, range, 60, 60);

  gradient = drawingContext.createRadialGradient(p1.x, p1.y, 0, p2.x, p2.y, range * 2);


  for (let i = 0; i < 1.0; i += step) {
    gradient.addColorStop(i, generateColor(10));
  }

  drawingContext.fillStyle = gradient;

  rect(pos.x + range / 4, pos.y, range / 2.35, range, 50,50);

}

