function drawArc(x, y, mySize) {
  noStroke();
  setGradientFill(createVector(x - mySize / 2, y), createVector(x + mySize / 2, y))
  arc(x, y, mySize, mySize, PI, 2 * PI, OPEN);
  setGradientFill(createVector(x, y - mySize / 2), createVector(x, y + mySize / 2))
  arc(x, y, mySize, mySize, 0, PI, OPEN);

}


