function recursiveSquare(x,y,mySize,level)
{
  level -= 1;

  noStroke();
  fill(generateColor());
  push();
  translate(x,y);
  for(let yt = 0;yt < 20;yt+=2)
  {
    rect(-yt/2,-yt,mySize/2,mySize/2);
  }
  pop();
  if (level >= 0)
  {
    push();
    translate(x,y);
    rotate(random(0,2*PI));
    recursiveSquare(-mySize/8,0,mySize/2,level);
    recursiveSquare(mySize/8,0,mySize/2,level);
    recursiveSquare(0,-mySize/6,mySize/3,level);
    recursiveSquare(0,mySize/6,mySize/3,level);
    recursiveSquare(mySize/8,-mySize/6,mySize/6,level);
    recursiveSquare(mySize/8,mySize/6,mySize/6,level);
    recursiveSquare(-mySize/8,-mySize/6,mySize/6,level);
    recursiveSquare(-mySize/8,mySize/6,mySize/6,level);
    pop();
  }

}


