var myScale = 5;

let agent = [];

let border = -100;

function setup() {
	createCanvas(windowWidth, windowWidth);
	colorMode(HSB, 360, 100, 100);
	rectMode(CENTER);
	strokeCap(SQUARE);

	background(100);

	let mySize = width / 10;

	for (let x = 0; x <= width; x += mySize) {
		for (let y = 0; y <= height; y += mySize) {
			for (let i = 0; i < 2; i++) {
				agent.push(new Agent(x, y, mySize));
			}
		}
	}

	//noLoop();

	smooth(8);
}

function draw() {
	for (let i = 0; i < agent.length; i++) {
		agent[i].update();
	}
}

// painting agent 

class Agent {
	constructor(x, y, mySize) {
		this.p = createVector(x, y);
		//this.pOld = createVector(this.p.x, this.p.y);
		this.speed = 0.1;
		this.rad = mySize * 1.5;
		this.color = generateColor();
		this.fixColor = generateColor();
		this.direction = 1;
		this.myrand = random(0, 1);
		this.strokeWidth = 0.5;
	}

	getRad() {
		return this.rad;
	}

	update() {
		if ((frameCount % 100) == 0) {
			//this.direction = random([-1,1]);
			this.color = generateColor();
			this.rad /= 1.1;
		}

		this.p.x += this.direction * vector_field(this.p.x, this.p.y).x * this.speed;
		this.p.y += this.direction * vector_field(this.p.x, this.p.y).y * this.speed;

		//noStroke();
		stroke(this.color);

		if (this.rad > 10) {
			fill(this.color);
		} else {
			this.speed = 0;
		}
		ellipse(this.p.x, this.p.y, this.rad, this.rad);
	}
}

// vector field function 
// the painting agents follow the flow defined 
// by this function 

function vector_field(x, y) {
	//myScale = map(-10,10,x,0,width);
	x = map(x, 0, width, 0, myScale);
	y = map(y, 0, height, 0, myScale);

	let k1 = 5;
	//let k1 = map(x,-myScale,myScale,-20,20);
	let k2 = 3;
  //let k2 = map(y,-myScale,myScale,-20,20);

	let u = sin(k1 * y) + cos(k2 * y);
	let v = sin(k2 * x) - cos(k1 * x);

	return createVector(u, v);
}

function generateColor() {
	return random(colors);
}

