    this.dir = random([-1,1]);
    this.freq = 0.;
    this.speed = createVector(mySpeed.x,mySpeed.y);
    this.route = [[this.x,this.y]];
  }

  display()
  {
    noFill();
    stroke(100);
    strokeWeight(this.width);
    push();
    translate(this.x,this.y);
    rotate(this.angle);
    clipShape(0,0,this.size);
    stroke(100);
    strokeWeight(this.width/1.01);
    clipShape(0,0,this.size);
    pop();
  }


  move()
  {

    this.angle += PI/100;
    this.x += this.speed.x
    this.y += this.speed.y;

  }

}




function clipShape(xc, yc, mySize) {
  let x;
  let y;

  beginShape();

  let r1 = mySize;

  vertex(xc + r1, yc - r1 * 5);

  // arc1 


  for (let ang = 0; ang < PI; ang += (2 * PI) / 100) {
    x = xc + r1 * cos(ang);
    y = yc + r1 * sin(ang);
    vertex(x, y);
  }

  vertex(xc - r1, yc);

  vertex(xc - r1, yc - r1 * 6.25);


  let r2 = 1.5 * r1;

  for (let ang = PI; ang < 2 * PI; ang += (2 * PI) / 100) {
    x = xc + r1 / 2 + r2 * cos(ang);
    y = yc + r2 * sin(ang) - r1 * 6.25;
    vertex(x, y);
  }

  vertex(xc + r2 + r1 / 2, yc - r1 * 6.25)
  vertex(xc + r2 + r1 / 2, yc + r1 * 1.875);



  r3 = 2 * r1;

  for (let ang = 0; ang < PI; ang += (2 * PI) / 100) {
    x = xc + r3 * cos(ang);color(0);
    y = yc + r1 * 1.875 + r3 * sin(ang);
    vertex(x, y);
  }

  vertex(xc - r3, yc + r1 * 1.875);
  vertex(xc - r3, yc - r1 * 5);


  endShape();

}


