class Particle {
	constructor(x,y,myThick,myScale)
	{
		this.p = createVector(x,y);
		this.pOld = createVector(this.p.x,this.p.y);
		this.thick = myThick;
		this.speed = 1;
		this.color = generateColor();
		this.direction = -1;
		this.scale = myScale;
	}

	update() {
		this.p.x += this.speed * this.direction * vector_field(this.p.x,this.p.y,this.scale).x;
		this.p.y += this.speed * this.direction * vector_field(this.p.x,this.p.y,this.scale).y;
		strokeWeight(this.thick);
		stroke(this.color);
		line(this.pOld.x,this.pOld.y,this.p.x,this.p.y);
		stroke(this.color);
		this.pOld.set(this.p);
	}
}

function vector_field(x, y, myScale) {
	x = map(x, border, width-border, 0, myScale);
	y = map(y, border, height-border, 0, myScale);
	u = -2.0 * (floor(y) % 2.0) + 1.0;
	v = -2.0 * (floor(x) % 2.0) + 1.0;
	return createVector(u,v);
}

