function polarSquare(x,y,signo,mySize)
{
	let freq = signo*0.01;
	noFill();
	fill(colors[0]);
	square(x,y,mySize*2);
	stroke(100);
	noFill();
	square(x,y,mySize*2.1);
	stroke(0);
	fill(colors[1]);
	circle(x,y,mySize/2);
	for(let ang=0; ang <= 2*PI; ang += (2*PI)/5)
	{
		let r = min(1/abs(cos(ang + frameCount*freq)),1./abs(sin(ang + frameCount*freq)));
		let xx = x + mySize*r*cos(ang + frameCount*freq);
		let yy = y + mySize*r*sin(ang + frameCount*freq);
		fill(generateColor());
		fill(colors[2]);
		line(x,y,xx,yy);
		push();
		translate(xx,yy);
		rotate(frameCount*0.005);
		circle(0,0,mySize/3.5);
      pop();		
	}
}

