function getTrajectory(xc,yc,mySize)
{
  let coords = [];

  let r1 = mySize;

  // line1

  for(temp=5;temp >= 0;temp-=0.5)
  {
    coords.push([xc + r1,yc - r1*temp]);
  }

  // arc1

  for(let ang=0;ang <= PI;ang+=(2*PI)/15)
  {
    x = xc + r1*cos(ang);
    y = yc + r1*sin(ang);
    coords.push([x,y]);
  }

  // line2 

  for(let temp=0;temp < 6.5;temp+=0.5)
  {
    coords.push([xc - r1,yc - r1*temp]);
  }

  // arc2 

  let r2 = 1.5*r1;

  for(let ang=PI;ang <= 2*PI;ang+=(2*PI)/20)
  {
    x = xc + r1/2 + r2*cos(ang);
    y = yc + r2*sin(ang) - r1*6.25;
    coords.push([x,y]);
  }

  // line3

  for(let temp=-6;temp <= 1.875;temp+=0.5)
  {
    coords.push([xc + r2 + r1/2,yc + r1*temp]);
  }

  r3 = 2*r1;

  // arc3

  for(let ang=0;ang <= PI;ang+=(2*PI)/20)
  {
    x = xc + r3*cos(ang);
    y = yc + r1*1.875 + r3*sin(ang);
    coords.push([x,y]);
  }

  // line4

  for(let temp = 1.877;temp > -5.5;temp-=0.5)
  {
    coords.push([xc - r3,yc + r1*temp]);
  }

  return coords;

}

function clipShape(xc,yc,mySize)
{
  let x;
