function drawSine(p1, p2,freq,amp)
{
  let d = p1.dist(p2);
  let a = atan2(p2.y-p1.y,p2.x-p1.x);
  push();
  translate(p1.x,p1.y);
  rotate(a);
	let xx,yy;
	curveTightness(1);
	beginShape();
	for (let i = 0; i <= d; i += 1) {
			xx = i;
		  arg = xx*TWO_PI*freq/d; 
			yy = sin(arg)*amp - sin(arg/2)*amp/4;
			vertex(xx,yy);
		}
	endShape();
	fill(0);
	pop();
}

function drawShape(xc,yc,mySize)
{
	noStroke();
	curveTightness(1);
	fill(generateColor(1));
	beginShape();
	let n = floor(random(3,15));
	for (let ang = 0; ang <= 2*PI; ang += (2*PI)/500) 
	{
		let rad = mySize + (mySize)*cos(n*ang);
		let x = xc + rad*cos(ang);
		let y = yc + rad*sin(ang);
		vertex(x,y);
  }
	endShape();
}

