}

function myRandom(colors, weights) {
	let sum = 0;
	for (let i = 0; i < colors.length; i++) {
		sum += weights[i];
	}
	let rr = random(0, sum);
	for (let j = 0; j < weights.length; j++) {
		if (weights[j] >= rr) {
			return colors[j];
		}
		rr -= weights[j];
	}
}

class Agent {
	constructor() {
		this.p = createVector(random(border, width - border), random(border, height - border));
		this.color = generateColor(10);
		this.sizeX = random(1,300);
		this.sizeY = this.sizeX;
		this.fill = random(1.0);
		this.blend = random(1.0);
		this.speed = 0.0;
		this.scale = 5;
		this.angle = 0.0;
		if (random(0,1) < 0.001) {
			this.dot = true;
		} else {
			this.dot = false;
		}
		if (random(0,1) > 0.5) {
			this.speed = 0.1;
			this.scale = 0;
			this.period = 0.0;
			this.direction = 1;
			this.shape = "ellipse";
		} else {
			this.shape = "rectangle";
			this.direction = -1;
		}
		this.scalar = random(1.0,50);
		this.step = random(1,2);
		this.stroke = generateColor(50);
		this.strokeWidth = random(0,2);
		this.period = random(0.01,0.05);
	}

	getPartner() {
		return this.partner;
	}

	getP() {
		return this.p;
	}

	getColor() {
		return this.color;
	}

	update() {
		this.p.x += vector_field(this.p.x, this.p.y, this.scale).x * this.speed;
		this.p.y += vector_field(this.p.x, this.p.y, this.scale).y * this.speed;
		if (this.dot) {
			blendMode(BLEND);
			fill(0,0,0);
		} else {
			blendMode(DIFFERENCE);
			let gradient = drawingContext.createRadialGradient(this.p.x, this.p.y, 0, this.p.x, this.p.y, this.sizeX);
			for (let i = 0; i <= 1; i += 0.5) {
				gradient.addColorStop(i, generateColor(1));
			}
			drawingContext.fillStyle = gradient;
		}
		strokeWeight(this.strokeWidth);
		stroke(255);
		if (this.shape == "rectangle") {
			rect(this.p.x, this.p.y, this.sizeX + this.scale * sin(frameCount * this.period), this.sizeY + this.scale * sin(frameCount * this.period), 20, 20);
		} else {
			ellipse(this.p.x, this.p.y, this.sizeX + this.scale * sin(frameCount * this.period), this.sizeY + this.scale * sin(frameCount * this.period));
		}
	}
}

function vector_field(x, y, myScale) {
	x = map(x, 0, width, 0, myScale);
	y = map(y, 0, height, 0, myScale);
	let k1 = 5;
	let k2 = 3;
	let u = cos(k1 * y) + cos(k2 * y);
	let v = sin(k2 * x) - cos(k1 * x);
	return createVector(u, v);
}

