function eightRing(x, y, d, col1,col2) {
    const thick = d/10;
    const r = d/2;
    const rOut = r + thick/2;
    const rIn  = max(1, r - thick/2);
    const cxL = x - d/2;
    const cxR = x + d/2;
    const da = 0.03;

    fill(col1);
    circle(x - d/2,y,d);
    fill(col1);
    circle(x + d/2,y,d);

    noStroke();
    fill(col2);

    beginShape();
    for (let a = -PI; a <= 0; a += da) {
        vertex(cxL + rOut * cos(a), y + rOut * sin(a));
    }
    for (let a = -PI; a <= 0; a += da) {
        vertex(cxR + rOut * cos(a), y + rOut * sin(a));
    }
    for (let a = 0; a <= PI; a += da) {
        vertex(cxR + rOut * cos(a), y + rOut * sin(a));
    }
    for (let a = 0; a <= PI; a += da) {
        vertex(cxL + rOut * cos(a), y + rOut * sin(a));
    }

    beginContour();
    for (let a = TWO_PI; a >= 0; a -= da) {
        vertex(cxR + rIn * cos(a), y + rIn * sin(a));
    }
    endContour();

    beginContour();
    for (let a = TWO_PI; a >= 0; a -= da) {
        vertex(cxL + rIn * cos(a), y + rIn * sin(a));
    }
    endContour();

    endShape(CLOSE);
}

